import React from 'react'

export default function AssetRow({ asset }) {
  const pct = (key) => (
    <td className={asset[key] >= 0 ? 'text-green-600' : 'text-red-600'}>
      {asset[key]}%
    </td>
  )

  return (
    <tr>
      <td>{asset.id}</td>
      <td><img src={asset.logo} alt="" className="h-6"/></td>
      <td>{asset.name}</td>
      <td>{asset.symbol}</td>
      <td>${asset.price.toLocaleString()}</td>
      {pct('1h')}{pct('24h')}{pct('7d')}
      <td>{asset.marketCap}</td>
      <td>{asset.volume24h}</td>
      <td>{asset.circulating}</td>
      <td>{asset.maxSupply}</td>
      <td><img src={asset.chart7d} alt="" className="h-6"/></td>
    </tr>
  )
}
