import React from 'react'

export default function AssetRow({ asset, index }) {
  const pct = (key) => (
    <td className={`${asset[key] >= 0 ? 'text-green-500' : 'text-red-500'} text-right`}>
      {asset[key] >= 0 ? '↑' : '↓'} {Math.abs(asset[key])}%
    </td>
  )

  return (
    <tr className="border-b border-gray-200 hover:bg-gray-50">
      <td className="py-4 px-2">{index + 1}</td>
      <td className="flex items-center gap-2">
        <img src={asset.logo} alt="" className="w-8 h-8"/>
        <span className="font-medium">{asset.name}</span>
        <span className="text-gray-500">{asset.symbol}</span>
      </td>
      <td className="text-right font-medium">${asset.price.toLocaleString()}</td>
      {pct('1h')}
      {pct('24h')}
      {pct('7d')}
      <td className="text-right">${asset.marketCap}</td>
      <td className="text-right">${asset.volume24h}</td>
      <td className="text-right">{asset.circulating}</td>
      <td className="text-right">{asset.maxSupply}</td>
      <td><img src={asset.chart7d} alt="" className="h-10 w-24"/></td>
    </tr>
  )
}
