import { useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { updateAsset } from '../features/assets/assetsSlice'

export default function PriceUpdater() {
  const dispatch = useDispatch()
  const assets = useSelector(state => state.assets)

  useEffect(() => {
    const interval = setInterval(() => {
      const randomChange = () => (Math.random() - 0.5) * 1.5
      const asset = assets[Math.floor(Math.random() * assets.length)]
      
      dispatch(updateAsset({
        symbol: asset.symbol,
        changes: {
          price: asset.price * (1 + randomChange()/100),
          '1h': asset['1h'] + randomChange()/3,
          '24h': asset['24h'] + randomChange()/2,
          '7d': asset['7d'] + randomChange()/5,
          volume24h: `$${(parseFloat(asset.volume24h) * (1 + randomChange()/200)).toFixed(2)}B`,
          marketCap: `$${(parseFloat(asset.marketCap) * (1 + randomChange()/300)).toFixed(2)}B`
        }
      }))
    }, 1000)

    return () => clearInterval(interval)
  }, [dispatch, assets])

  return null
}
