import React from 'react'

export default function AssetRow({ asset, index }) {
  const formatPercentage = (value) => {
    const isPositive = value >= 0
    return (
      <td className={`${isPositive ? 'text-green-500' : 'text-red-500'} text-right px-4 py-4`}>
        {isPositive ? '↑' : '↓'} {Math.abs(value).toFixed(2)}%
      </td>
    )
  }

  return (
    <tr className="border-b hover:bg-gray-50">
      <td className="px-4 py-4">{index + 1}</td>
      <td className="px-4 py-4 flex items-center gap-3">
        <img src={asset.logo} alt={asset.name} className="w-8 h-8" />
        <div>
          <div className="font-medium">{asset.name}</div>
          <div className="text-gray-500 text-sm">{asset.symbol}</div>
        </div>
      </td>
      <td className="px-4 py-4 text-right font-medium">${asset.price.toLocaleString()}</td>
      {formatPercentage(asset['1h'])}
      {formatPercentage(asset['24h'])}
      {formatPercentage(asset['7d'])}
      <td className="px-4 py-4 text-right">${asset.marketCap}</td>
      <td className="px-4 py-4 text-right">${asset.volume24h}</td>
      <td className="px-4 py-4 text-right">{asset.circulating}</td>
      <td className="px-4 py-4 text-right">{asset.maxSupply}</td>
      <td className="px-4 py-4">
        <img 
          src={asset.chart7d} 
          alt="7d chart" 
          className="h-8 w-32 object-cover" 
          style={{ 
            filter: asset['7d'] >= 0 ? 'invert(47%) sepia(95%) saturate(427%) hue-rotate(86deg) brightness(96%) contrast(91%)' : 'invert(33%) sepia(95%) saturate(1519%) hue-rotate(332deg) brightness(87%) contrast(96%)'
          }} 
        />
      </td>
    </tr>
  )
}
