import { useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { updateAsset } from '../features/assets/assetsSlice'

const randomChange = () => (Math.random() - 0.5) * 2 // ±1%

export default function PriceUpdater() {
  const dispatch = useDispatch()
  const assets = useSelector(s => s.assets)

  useEffect(() => {
    const iv = setInterval(() => {
      const a = assets[Math.floor(Math.random()*assets.length)]
      dispatch(updateAsset({
        symbol: a.symbol,
        changes: {
          price: +(a.price * (1 + randomChange()/100)).toFixed(2),
          '1h': +(a['1h'] + randomChange()).toFixed(2),
          '24h': +(a['24h'] + randomChange()).toFixed(2),
          volume24h: `${(parseFloat(a.volume24h) * (1 + randomChange()/100)).toFixed(2)}B`
        }
      }))
    }, 1500)
    return () => clearInterval(iv)
  }, [assets, dispatch])

  return null
}
